// ==UserScript==
// @name         RMP WatchðŸŽ¥ IMDb & Kinopoisk
// @namespace    rmp-watch-imdb-kinopoisk
// @version      6
// @description  Opens RMP website in a new tab with movie or series on IMDb and Kinopoisk
// @icon         https://github.com/RRRiderrr/rmp/raw/main/icons/favicon-32x32.png
// @author       Rider
// @updateURL    https://raw.githubusercontent.com/RRRiderrr/rmp/master/tms.user.js
// @downloadURL  https://raw.githubusercontent.com/RRRiderrr/rmp/master/tms.user.js
// @match        https://www.imdb.com/title/*
// @match        https://www.kinopoisk.ru/*
// @grant        GM_addStyle
// ==/UserScript==

(function () {
  'use strict';

  const iconBase64 = 'data:image/png;base64,';

  function createRmpButton(movieId) {
    const button = document.createElement('button');
    button.style.position = 'fixed';
    button.style.bottom = '10px';
    button.style.right = '10px';
    button.style.zIndex = '999999';
    button.style.width = '40px';
    button.style.height = '40px';
    button.style.border = 'none';
    button.style.borderRadius = '50%';
    button.style.cursor = 'pointer';
    button.style.background = 'transparent';
    button.style.boxShadow = '0px 3px 6px rgba(0, 0, 0, 0.3)';
    button.style.backgroundColor = 'white';
    button.style.color = 'gray';
    button.style.padding = '0';
    button.style.transition = 'transform 0.2s';

    const icon = document.createElement('img');
    icon.src = iconBase64;
    icon.style.width = '100%';
    icon.style.height = '100%';
    icon.style.borderRadius = '50%';
    button.appendChild(icon);

    button.addEventListener('click', () => {
      window.open(`https://rmp.pages.dev/#${movieId}`, '_blank');
    });

    button.addEventListener('mouseenter', () => {
      button.style.transform = 'scale(1.1)';
    });

    button.addEventListener('mouseleave', () => {
      button.style.transform = 'scale(1)';
    });

    return button;
  }

  function insertRmpButton() {
    const movieId = extractMovieId();
    if (movieId) {
      const button = createRmpButton(movieId);
      document.body.appendChild(button);
    }
  }

  function extractMovieId() {
    const imdbMatch = window.location.pathname.match(/^\/title\/(tt\d+)\/?/);
    if (imdbMatch) {
      return imdbMatch[1];
    }

    const kinopoiskMatch = window.location.pathname.match(/^\/(film|series)\/(\d+)\/?/);
    if (kinopoiskMatch) {
      return kinopoiskMatch[2];
    }

    return null;
  }

  insertRmpButton();
})();
